import socket
import re
import math

def collect_values(count=20):
    values = []
    try:
        s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        s.connect(('localhost', 5000))

        while True:
            data = s.recv(4096).decode()
            if ">" in data: break

        for i in range(count):
            s.sendall(b'1\n')
            response = b""
            while b">" not in response:
                chunk = s.recv(4096)
                if not chunk: break
                response += chunk

            data = response.decode()
            n_match = re.search(r'N = (\d+)', data)
            e_match = re.search(r'e = (\d+)', data)
            c_match = re.search(r'c = (\d+)', data)

            if n_match and e_match and c_match:
                values.append((int(n_match.group(1)), int(e_match.group(1)), int(c_match.group(1))))
        s.close()
    except Exception as e:
        print(f"Error: {e}")
    return values

samples = collect_values(20)

for i in range(len(samples)):
    for j in range(i + 1, len(samples)):
        n1 = samples[i][0]
        n2 = samples[j][0]
        if n1 == n2:
            print(f"Collision found! Sample {i} and {j} have same N.")
            e1 = samples[i][1]
            e2 = samples[j][1]
            print(f"e1 = {e1}")
            print(f"e2 = {e2}")
            if e1 != e2:
                g = math.gcd(e1, e2)
                print(f"gcd(e1, e2) = {g}")
                if g == 1:
                    print("Exploitable Common Modulus!")
                    # Solve
                    # Find a, b such that a*e1 + b*e2 = 1
                    # c1^a * c2^b = m (mod N)
                    N = n1
                    c1 = samples[i][2]
                    c2 = samples[j][2]

                    # Extended Euclidean Algorithm
                    def egcd(a, b):
                        if a == 0:
                            return (b, 0, 1)
                        else:
                            g, y, x = egcd(b % a, a)
                            return (g, x - (b // a) * y, y)

                    g, a, b = egcd(e1, e2)
                    print(f"Bezout coefficients: a={a}, b={b}")

                    # Calculate m
                    # if a < 0, c1^a = (c1^-1)^(-a)
                    if a < 0:
                        try:
                            c1_inv = pow(c1, -1, N)
                            term1 = pow(c1_inv, -a, N)
                        except ValueError:
                            print("Inverse failed")
                            continue
                    else:
                        term1 = pow(c1, a, N)

                    if b < 0:
                        try:
                            c2_inv = pow(c2, -1, N)
                            term2 = pow(c2_inv, -b, N)
                        except ValueError:
                            print("Inverse failed")
                            continue
                    else:
                        term2 = pow(c2, b, N)

                    m = (term1 * term2) % N
                    try:
                        print(f"Message: {m.to_bytes((m.bit_length()+7)//8, 'big')}")
                    except:
                        print(f"Message (int): {m}")

            else:
                print("Same N and same e. Not useful.")
